mapper.select:

select articleNO, parentNO, title, content, NVL(imageFileName, 'null') as imageFileName, id, writeDate
from t_board
where articleNO=#{articleNo}

mapper.update:
update t_board
set title=#{title},
	content=#{content}
	where articleNO = #{articleNO}

update.foreach:
update t_imagefile
set imageFileName = #{item.imageFileName}
where articleNO = #{item.articleNO}
